var today = new Date();
var day = today.getDay(), diff = today.getDate() - day + (day == 0? -6:1);
var monday = new Date(today.setDate(diff));
var friday = new Date();
friday.setDate(monday.getDate() + 4);
var results = new Intl.DateTimeFormat('en-AU', {day: 'numeric', month: 'short'}).format(friday) + " " + monday.getYear() + " " + monday.getMonth();

var kme = Application("Keyboard Maestro Engine");
kme.setvariable('Week Text', { to: results });
