(function () {
    'use strict';

    var a = Application.currentApplication(),
        sa = (a.includeStandardAdditions = true, a),
        strCMD = Application("Finder")
        .selection()
        .reduce(function (a, x) {
            return a + '/opt/homebrew/bin/code "' + x.url()
                .slice(7) + '"\n';
        }, '');


    //return strCMD
    sa.doShellScript(strCMD);

    Application('Visual Studio Code')
        .activate();
})();
