const purgeCommentsFromList = list => {
    // Remove HTML comments, including multi-line comments
    list = list.replace(/<!--[\s\S]*?-->/g, '');

    // Split the list into lines
    return list.split('\n')
               .map(line => line.trim()) // Trim each line
               .filter(line => line.length > 0 && !line.startsWith('#') && !line.startsWith('//')) // Remove blank lines, lines starting with #, and lines starting with //
               .map(line => {
                   const commentIndex = line.indexOf(' //');
                   return commentIndex !== -1 ? line.slice(0, commentIndex).trim() : line;
               }) // Remove in-line comments that start with ' // ' and trim again
               .filter(line => line.length > 0) // Remove any lines that became empty after removing in-line comments
               .join('\n');
}

var theList = kmvar.local_List;

const preparedList = purgeCommentsFromList(theList);

return preparedList;

