function createJsonFromMarkdownLinks(markdownLinks) {
    const result = [];

    const lines = markdownLinks.split('\n');

    lines.forEach(line => {
        const match = line.match(/\[(.*?)\]\((.*?)\)/);
        if (match) {
            const title = match[1];
            const url = match[2];

            result.push({
                "title": title,
                "tags": [
                    [
                        "mlt"
                    ]
                ],
                "url": url
            });
        }
    });

    return JSON.stringify(result, null, 2);
}

var theList = kmvar.local_ToProcessList;

const theJSON = createJsonFromMarkdownLinks(theList);

return theJSON;

