(() => {
    "use strict";

    // Rob Trew @2021

    // Value of KM mdLink variable placed in clipboard,
    // both as plain text,
    // and also as a labelled RTF hyperlink.

	// modified: 2022-01-17, J. Sauer
	//   a) Since mhLink (text content) can be HTML or Markdown
	//      added‚Äî
	//        mh = Application("Keyboard Maestro Engine").getvariable("mhLink")
	//   b) For Rich text variant: added accomodation for HTML Style; spaces before and after the link

    ObjC.import("AppKit");

    // main :: IO ()
    const main = () => {
        const
            md = Application("Keyboard Maestro Engine")
            .getvariable("mdLink"),
            mh = Application("Keyboard Maestro Engine")
            .getvariable("mhLink"),
            style = Application("Keyboard Maestro Engine")
            .getvariable("lnkStyle"),
            kv = mdLinkParts(md),
            label = htmlEncoded(kv[0]);

        return (
            copyTypedString(true)(
                "public.utf8-plain-text"
            )(mh),
            copyTypedString(false)(
                "public.rtf"
            )(
                either(x => x)(x => x)(
                    rtfFromHTML(
                        // The url field is not empty ?
                        0 !== kv[1].length ? (
                           style +
                            `\n` +
                            `<td>&nbsp<a href="${kv[1]}">` + (
                                `${label}</a>&nbsp</td>`
                            )
                        ) : `<p>${label}</p>`
                    )
                )
            ),
            md
        );
    };


    // ---------------------- LINKS ----------------------

    // copyTypedString :: Bool -> String -> String -> IO ()
    const copyTypedString = blnClear =>
        // public.html, public.rtf, public.utf8-plain-text
        pbType => s => {
            const pb = $.NSPasteboard.generalPasteboard;

            return (
                blnClear && pb.clearContents,
                pb.setStringForType(
                    $(s),
                    $(pbType)
                )
            );
        };


    // htmlEncoded :: String -> String
    const htmlEncoded = s => {
        const rgx = /[\w\s]/u;

        return [...s].map(
            c => rgx.test(c) ? (
                c
            ) : `&#${c.codePointAt(0)};`
        ).join("");
    };


    // mdLinkParts :: String -> (String, String)
    const mdLinkParts = s => {
        const ab = s.trim().split("](");

        return 2 !== ab.length ? (
            Tuple(s)("")
        ) : Tuple(ab[0].slice(1))(
            ab[1].slice(0, -1)
        );
    };


    // rtfFromHTML :: String -> Either String String
    const rtfFromHTML = strHTML => {
        const
            as = $.NSAttributedString.alloc
            .initWithHTMLDocumentAttributes($(strHTML)
                .dataUsingEncoding($.NSUTF8StringEncoding),
                0
            );

        return bindLR(
            "function" !== typeof as
            .dataFromRangeDocumentAttributesError ? (
                Left("String could not be parsed as HTML")
            ) : Right(as)
        )(
            // Function bound if Right value obtained above:
            htmlAS => {
                const
                    error = $(),
                    rtfData = htmlAS
                    .dataFromRangeDocumentAttributesError({
                            "location": 0,
                            "length": htmlAS.length
                        }, {
                            DocumentType: "NSRTF"
                        },
                        error
                    );

                return Boolean(
                    ObjC.unwrap(rtfData) && !error.code
                ) ? Right(
                    ObjC.unwrap($.NSString.alloc
                        .initWithDataEncoding(
                            rtfData,
                            $.NSUTF8StringEncoding
                        ))
                ) : Left(ObjC.unwrap(
                    error.localizedDescription
                ));
            }
        );
    };


    // --------------------- GENERIC ---------------------

    // Left :: a -> Either a b
    const Left = x => ({
        type: "Either",
        Left: x
    });

    // Right :: b -> Either a b
    const Right = x => ({
        type: "Either",
        Right: x
    });

    // bindLR (>>=) :: Either a ->
    // (a -> Either b) -> Either b
    const bindLR = m =>
        mf => m.Left ? (
            m
        ) : mf(m.Right);

    // either :: (a -> c) -> (b -> c) -> Either a b -> c
    const either = fl =>
        // Application of the function fl to the
        // contents of any Left value in e, or
        // the application of fr to its Right value.
        fr => e => e.Left ? (
            fl(e.Left)
        ) : fr(e.Right);

    // Tuple (,) :: a -> b -> (a, b)
    const Tuple = a =>
        b => ({
            type: "Tuple",
            "0": a,
            "1": b,
            length: 2
        });

    return main();
})();

function prependTextToFileContents(fileContents, textToPrepend) {

  const fileLines = fileContents.split('\n');
  const textLines = textToPrepend.split('\n');

  let outputFileLines = [];
  let inHeaderComment = false;
  let textPrepended = false;

  for (let fileLine of fileLines) {
    if (!textPrepended) {
      // If inside the header comment
      if (inHeaderComment) {
        outputFileLines.push(fileLine);
        // Check if the current line ends the header comment
        if (fileLine.endsWith('-->')) {
          inHeaderComment = false;
          outputFileLines.push(...textLines);
          textPrepended = true;
        }
      } else {
        // Check if the current line starts a header comment
        if (fileLine.trim().startsWith('<!--')) {
          inHeaderComment = true;
          outputFileLines.push(fileLine);
        } else {
          // If not inside a header comment, prepend the text lines immediately below the header comment
          outputFileLines.push(...textLines);
          outputFileLines.push(fileLine);
          textPrepended = true;
        }
      }
    } else {
      outputFileLines.push(fileLine);
    }
  }

  // If there was no header comment and the lines haven't been prepended, prepend the lines at the top
  if (!textPrepended) {
    outputFileLines = [...textLines, ...outputFileLines];
  }

  return outputFileLines.join('\n');
}

var fileContents = kmvar.local_FileContents;
var textToPrepend = kmvar.local_TextToPrepend;

const updatedFileContents = prependTextToFileContents(fileContents, textToPrepend);

return updatedFileContents;
